$(document).ready(function(){new Swiper(".swiper-container",{pagination:".swiper-pagination",slidesPerView:"auto",nextButton:".swiper-button-next",prevButton:".swiper-button-prev",parallax:!0,paginationBulletRender:function(e,n,t){return'<div class="'+t+'"><span class="pagindex-container">0'+(n+1)+"</span></div>"},speed:800,autoplay:8e3,keyboardControl:!0,mousewheelControl:!0,mousewheelSensitivity:.5,spaceBetween:200,centeredSlides:!0,breakpoints:{900:{pagination:""}}});$("a").each(function(){new RegExp("/"+window.location.host+"/").test(this.href)||$(this).click(function(e){e.preventDefault(),e.stopPropagation(),window.open(this.href,"_blank")})})});